def find_primes(n):
"""Finds all prime numbers up to n using the Sieve of Eratosthenes."""
if n < 2:
return []
is_prime = [True] * (n + 1)
is_prime[0] = is_prime[1] = False
for p in range(2, int(n**0.5) + 1):
if is_prime[p]:
